// src/components/Register.jsx
import React, { Component } from "react";
import axios from "axios";
import Alert from "./Alert";
//import "./Login.css";
import { login } from "../login";
import { check } from "../login";
import "./app.css";

class Login extends Component {
  state = { err: "" };

  componentDidMount() {
    check().then((r) => {
      if (r) {
        window.location = "/";
      }
    });
  }

  login = (e) => {
    e.preventDefault();
    login(
      document.getElementById("email").value,
      document.getElementById("password").value
    ).then((r) => {
      if (r === true) {
        window.location = "/";
      } else {
        this.setState({ err: r });
      }
    });
  };

  render() {
    return (
      <div class="showcase">
        <div className="grandParentContaniner">
          <div class="w3-cell-row">
            <div class="w3-third w3-container w3-Deep Purple">
              <p></p>
            </div>

            <div class="w3-third w3-container w3-black">
              <div className="parentContainer">
                <form onSubmit={this.login}>
                  <h1 className="w3-center">Welcome to Scry</h1>

                  <div className="w3-card-4" style={{ margin: "2rem" }}>
                    <div className="w3-container w3-black w3-center w3-xlarge">
                      Login
                    </div>
                    <div className="w3-container">
                      <p>
                        <label htmlFor="email">Email</label>
                        <input
                          type="text"
                          name="email"
                          class="w3-input w3-border"
                          id="email"
                        />
                      </p>
                      <p>
                        <label htmlFor="password">Password</label>
                        <input
                          type="password"
                          name="password"
                          class="w3-input w3-border"
                          id="password"
                        />
                      </p>
                      <div className="forgot">
                        <a href="reset_password" style={{ color: "#0d93ff" }}>
                          Forgot Password?
                        </a>
                      </div>
                      <p>
                        <button type="submit" class="w3-button w3-blue">
                          Sign In
                        </button>
                        {this.state.login && <p>You're logged in!</p>}
                      </p>
                      <p className="signUp">
                        Dont have an account?{" "}
                        <a href="sign_up" style={{ color: "#0d93ff" }}>
                          SIGN UP
                        </a>
                      </p>
                    </div>
                  </div>
                </form>
                {this.state.err.length > 0 && (
                  <Alert
                    message={`Check your form and try again! (${this.state.err})`}
                  />
                )}
              </div>
            </div>
            <div class="w3-third w3-container w3-black">
              <p></p>
            </div>
          </div>
        </div>
        <div>
          <img
            className="mx-auto rounded-circle"
            src={require("./assets/img/space.jpg")}
            alt=""
          />
          <h1>hele</h1>
        </div>
      </div>
    );
  }
}

export default Login;
